import React, { Component } from 'react';
import Inputitem from "./inputitem";
import "./inputlood.css"

class Inputlood extends Component {
    constructor(props){
        super(props);
        this.state={
            list:[
                "aaa",
                "bbb",
            ],
            newValue:'',
        }
        this.handInputValue=this.handInputValue.bind(this);
        this.dleButtonClick=this.handleButtonClick.bind(this);
        this.onDelete=this.onDelete.bind(this);

    }
    handInputValue(e){
        this.setState({
            newValue:e.target.value,
        });      
    }
    handleButtonClick(){
        this.setState({
            list: [...this.state.list,this.state.newValue],
            newValue:'',
        });
    }
    onDelete(index){
        const listd=[...this.state.list];
        listd.splice(index,1);
        this.setState({
            list:listd,
        })
    }
  render() {
    return (
      <div id="content">
        <input type="text" value={this.state.newValue} onChange={this.handInputValue}/>
        <button type="button" onClick={this.handleButtonClick}>add</button>
        <ul>
            {
                this.state.list.map((item,index) => {
                    return (
                        <Inputitem 
                        key={index}
                        content={item} 
                        index={index} 
                        onClick={this.onDelete}
                        />
                    )
                    // return <li key={index} onClick={this.onDelete.bind(this,index)}>{item}</li>
                })
            }
        </ul>
      </div>
    );
  }
}

export default Inputlood;
