import React, { Component } from 'react';
import Inputitem from "./inputitem";
import "./inputlood.css"

class Inputlood extends Component {
    constructor(props){
        super(props);
        this.state={
            list:[
                "aaa",
                "bbb",
            ],
            newValue:'',

        }
    }
    handInputValue(e){
        this.setState({
            newValue:e.target.value,
        });      
    }
    handleButtonClick(){
        this.setState({
            list: [...this.state.list,this.state.newValue],
            newValue:'',
        });
    }
    onDelete(index){
        const listd=[...this.state.list];
        listd.splice(index,1);
        this.setState({
            list:listd,
        })
    }
  render() {
    return (
      <div id="content">
        <input type="text" value={this.state.newValue} onChange={this.handInputValue.bind(this)}/>
        <button type="button" onClick={this.handleButtonClick.bind(this)}>add</button>
        <ul>
            {
                this.state.list.map((item,index) => {
                    return <Inputitem content={item}/>
                    // return <li key={index} onClick={this.onDelete.bind(this,index)}>{item}</li>
                })
            }
        </ul>
      </div>
    );
  }
}

export default Inputlood;
