import React, { Component } from 'react';
import "./inputlood.css"

class Inputlood extends Component {
    constructor(props){
        super(props);
        this.state={
            list:[
                "aaa",
                "bbb",
            ]
        }
    }
    handleButtonClick(){
        this.setState({
            list: [this.state.list,'ccc']
        });
    }
  render() {
    return (
      <div id="content">
        <input type="text"/>
        <button type="button" onClick={this.handleButtonClick}>add</button>
        <ul>
            {
                this.state.list.map((item) => {
                    return <li>{item}</li>
                })
            }
        </ul>
      </div>
    );
  }
}

export default Inputlood;
